import { useQuery } from '@apollo/react-hooks';
import gql from 'graphql-tag';
import Layout from '../components/MyLayout';
import Link from 'next/link';

export const TEST_QUERY = gql`
  query getPrimaryShop {
    primaryShop {
      _id
      name
    }
  }
`;

