export default function Blog() {
  const { loading, error, data } = useQuery(TEST_QUERY);

  if (loading) return <p>Loading</p>;
  if (error) return <p>ERROR</p>;

  console.log(data);

  return (
      <h1>Querying Data</h1>
  );
}
