import React from 'react';
import { ApolloProvider, getDataFromTree } from 'react-apollo';
import initApollo from './init-apollo';
import Head from 'next/head';
import hoistNonReactStatics from 'hoist-non-react-statics';

/**
 * Get the display name of a component
 * @name getComponentDisplayName
 * @param {React.Component} Component React component
 * @returns {String} Component display name
 */
const getComponentDisplayName = Component => {
  return Component.displayName || Component.name || 'Unknown';
}


/**
 * @name withApolloClient
 * @summary Wraps the component with a configured Apollo client provider
 * @param {React.Component} WrappedComponent Component to wrap
 * @returns {React.Component} Higher order component
 */
export default function withApolloClient(WrappedComponent) {
  class WithApolloClient extends React.Component {
    static async getInitialProps(ctx) {
      const { Component, router } = ctx;

      // TODO:
      // - pass prop data to url 
      // - check if getInitialProps was called without a requrest object
      // - parse user object

      const apollo = initApollo();

      ctx.ctx.apolloClient = apollo;

      let wrappedComponentProps = {};
      if (WrappedComponent.getInitialProps) {
        wrappedComponentProps = await WrappedComponent.getInitialProps(ctx);
      }

      // TODO: don't render if the response is finished while redirecting

      const apolloState = {};

      if (!process.browser) {
        // Run all GraphQL queries
        await getDataFromTree((
          <ApolloProvider client={apollo}>
            <WrappedComponent {...wrappedComponentProps} Component={Component} router={router} />
          </ApolloProvider>
        ));

        // getDataFromTree does not call componentWillUnmount
        // head sideeffect therefore need to be cleared manually
        Head.rewind();

        //Extract query data from the Apollo store
        apolloState.data = apollo.cache.extract();
      } else {
        apolloState.data = {};
      }

      return {
        ...wrappedComponentProps,
        apolloState
      };
    }
    static displayName = `WithApolloClient(${getComponentDisplayName(WrappedComponent)})`;

    // static propTypes = {
    //   apolloState: PropTypes.object.isRequired
    // };

    // TODO: getDerivedStateFromProps(nextProps)
    // - update routing store and rewrite routing path

    constructor(props) {
      super(props);
      // `getDataFromTree` renders the component first, then the client is passed off as a property.
      // After that, render is done using Next's normal rendering pipeline
      this.apollo = initApollo(props.apolloState.data);

      // State must be initialized if getDerivedStateFromProps is used
      this.state = {};
    }

    render() {
      console.log("Rendering with ApolloProvider")
      return (
        <ApolloProvider client={this.apollo}>
          <WrappedComponent {...this.props} />
        </ApolloProvider>
      );
    }
  }

  // Exclude copying `getInitialProps` because WithApolloClient ha its own
  hoistNonReactStatics(WithApolloClient, WrappedComponent, { getInitialProps: true });

  return WithApolloClient;
}
